definition.script:
pipeline{
    agent { label "ubuntu1604_azure" }

    environment {
        CI_JOB       = "CRI_CONTAINERD_K8S"
        CI           = "true"
        GOPATH       = "${WORKSPACE}/go"
        PATH         = "${GOPATH}/bin:/usr/local/go/bin:/usr/sbin:${PATH}"
        GOROOT       = "/usr/local/go"
        RUNTIME_PATH = "/usr/bin/kata-runtime"
        RUNTIME      = "kata-runtime"
    }

    stages {
        stage('Setup Environment') {
            steps {
                sh '''
                env
				cd $HOME
				git clone https://github.com/kata-containers/tests.git
				cd tests
				.ci/jenkins_job_build.sh "github.com/kata-containers/runtime"
                '''
            }
        }

        stage('Initialize Kubernetes') {
            steps {
                sh '''
                which crictl || true
                crictl --version || true
                cd "${WORKSPACE}/go/src/github.com/kata-containers/tests/integration/kubernetes"
                CRI_RUNTIME="containerd" ./init.sh || (sleep 10 && CRI_RUNTIME="containerd" ./init.sh)
                # Sleep 1 minute to let all k8s processes finish initialization
                sleep 60
                '''
            }
        }
        stage('Run e2e conformance') {
            steps {
                sh '''
                cd "${WORKSPACE}/go/src/github.com/kata-containers/tests/integration/kubernetes/e2e_conformance"
                sed -i '/pipefail/aset -x' run.sh
                sed -i 's/Eq/E/' run.sh
                sed -i '/done/asonobuoy status' run.sh
                ./run.sh
                '''
            }
        }
    }
}
